-- Function to create a level image
local function draw_map(level_id)
  
  -- Failsafe to avoid scanning a level that doesn't exist in the data table above
  if not level_data[level_id] then print(fmt("There's no level %02X in the data table!", level_id)) ; return end
  
  -- Get level dimensions in pixels based on the ID
  local level_width = level_data[level_id].width
  local level_height = level_data[level_id].height
  
  -- Failsafe to avoid any nil dimension
  if not level_width or not level_height then print(fmt("Level %02X data doesn't contain dimensions!", level_id)) ; return end
  
  -- Calculate derived dimensions
  local level_width_tiles = level_width/16
  local level_height_tiles = level_height/16
  local level_width_metatiles = level_width_tiles/4
  local level_height_metatiles = level_height_tiles/4
  
  -- Create map image handle
  local level_map_img = gd.createTrueColor(level_width, level_height) -- (x,y) start at (0,0)!!!!
  
  -- Make background transparent
  level_map_img:fill(1, 1, 0x123456) -- fill with a random colour I hope SNES can't produce
  level_map_img:colorTransparent(0x123456) -- and make it transparent
  
  -- Calculate metatile pages
  local metatile_pages = ceil(#level_data[level_id].metatiles/64)
  --print(fmt("metatile_pages = %d", metatile_pages)) -- DEBUG
  
  -- Load metatilemap images
  local metatilemap_img = {} -- 0 indexed
  for i = 1, metatile_pages do
    metatilemap_img[i-1] = gd.createFromPng(metatiles_path .. fmt("Level $%02X %02d.png", level_id, i-1))
  end
  --print(fmt("#metatilemap_img = %d", #metatilemap_img + 1)) -- DEBUG
  
  -- Loop thru the level layout data to read metatiles and properly draw the map image
  local metatile_id, xpos_dst, ypos_dst
  local index_in_metatileset, xpos_src, ypos_src
  local metatile_page
  for i = 1, #level_data[level_id].layout do
    -- Get metatile id
    metatile_id = level_data[level_id].layout[i]
    
    -- Calculate correct tile position in the whole map (destination)
    xpos_dst = 64*((i-1)%level_width_metatiles)
    ypos_dst = 64*floor((i-1)/level_width_metatiles)
    
    -- Loop to find the index in the metatileset
    index_in_metatileset = nil
    for j = 1, #level_data[level_id].metatiles do
      if metatile_id == level_data[level_id].metatiles[j] then
        index_in_metatileset = j -- 1 indexed
        break
      end
    end
    
    -- Failsafe to make sure the metatile id is in the metatileset
    if index_in_metatileset then -- ok
      -- Calculate metatile page
      metatile_page = ceil(index_in_metatileset/64) - 1 -- -1 to get the correct image name (0 indexed)
      
      -- Calculate the tile position in the metatileset image
      xpos_src = 64*(((index_in_metatileset-1)%64)%8)      --\ 8 because 8 columns of metatiles in metatileset
      ypos_src = 64*floor(((index_in_metatileset-1)%64)/8) --/
      
      -- Copy tile image to the map
      gd.copy(level_map_img, metatilemap_img[metatile_page], xpos_dst, ypos_dst, xpos_src, ypos_src, 64, 64)
    else -- metatile id missing from metatileset, so draw placeholder and warn
      level_map_img:filledRectangle(xpos_dst, ypos_dst, xpos_dst + 63, ypos_dst + 63, 0xFF00FF)
      print(fmt("Couldn't find tile $%04X image!", metatile_id))
    end
    --print(fmt("i:%03d index_in_metatileset = %d, metatile_page = %d, \"Level $%02X %02d.png\"", i, index_in_metatileset, metatile_page, level_id, metatile_page)) -- DEBUG 
  end

  -- Save image
  level_map_img:png(maps_path .. fmt("Level $%02X.png", level_id))
  print(fmt("\nLevel $%02X DONE!", level_id))
end


-- Run the maps!
draw_map(#level_data) -- #level_data to draw map from the last level in the table (level_data is 0 indexed, so # gets the real size-1)




















